import React, {useState} from 'react'
import Todo from './Todo'

function ToDoList(props){
    const [todoList, setToDoList] = useState([]);
    const [inputValues, setInputValues] = useState("")

    const handleInputChange=(event)=>{
        const {value} = event.target
        setInputValues(value)

    }

    const handleButtonClick=()=>{
        setToDoList(inputValues)
    }

    return(
        <div>
            Moja aplikacja Todo
            <input name="Todo Input" placeholder="Co będziemy dzisiaj robić?" value={inputValues} onChange={handleInputChange}></input>
            <button onClick={handleButtonClick}>Dodaj</button>
            {todoList.map((todo)=>(<Todo key={todo} todo={todo} />))}
        </div>
    )
}

export default ToDoList
