import React, {useState} from 'react'
import Todo from './Todo'
import Number from './Number'

function ToDoList(props){
    const [todoList, setToDoList] = useState([]);
    const [inputValues, setInputValues] = useState("")
    const [error, setError]=useState("")

    const handleInputChange=(event)=>{
        const {value} = event.target
        setInputValues(value)

    }

    const handleButtonClick=()=>{

        //blokada zeby nie wprowadzic pustego zadania
        if (!inputValues) return

        //jesli juz istnieje taki sam element jak teraz dodajemy
        if (todoList.some(todo=>todo===inputValues)){
            setToDoList(todoList)
            setError("To zadanie już istnieje")
            setInputValues("")

            return;
        }
        setToDoList([...todoList, inputValues]) //rozdzielenie tablicy na pojedyczne stringi i dodanie nowego na koniec
        setInputValues("")
        setError("")
    }

    //usuwanie!!!!!!!
    const handeToDoRemove=(toDoValues)=>{
        //setToDoList(todoList)
        //setInputValues(todoList.filter(todo=> todo!=toDoValues))

        //USUWANIE NIE DZIALA!!!!!!!
        //setToDoList([...todoList, todoList.filter(todo=>todo!=toDoValues)])
    }


    return(
        <div>
            <input type="text" name="item" placeholder="  add new item" value={inputValues} onChange={handleInputChange}></input>
            <button className="addButton" onClick={handleButtonClick}>Add</button>
            <div style={"clear: both;"}></div>
            <h3>My list</h3>
            <div className="listItem">
                {!!error &&
                    <p>{error}</p>
                }
                {todoList.map((todo)=>(<Todo key={todo} todo={todo} onCloseClick={handeToDoRemove}/>))}
            </div>
        </div>
    )
}

export default ToDoList
