import React, {useState} from 'react'
import Todo from './Todo'

function ToDoList(props){
    const [todoListState, setTodoListState] = useState({todos: [], inputValue: "", error: ""})

    const handleInputChange=(event)=>{
        const {value} = event.target
        //setInputValues(value)
        setTodoListState({
            ...todoListState, //zachowujemy to co było
            inputValue: value //nadpisujemy to co sie zmieniło
        })
    }

    const handleButtonClick=()=>{

        //blokada zeby nie wprowadzic pustego zadania
        if (!inputValues) return;

        //jesli juz istnieje taki sam element jak teraz dodajemy
        if (todoList.some(todo=>todo===inputValues)){ //funckja some sprawdza czy w tablicy jest juz taki element jak podajemy
            setTodoListState({
                ...todoListState,
                inputValue: "",
                error: "Ten produkt jest już na liście!"
            })
            return;
        }

        const {todos} = todoListState;
        setTodoListState({
            error: "",
            todos: [...todos, inputValue],
            inputValue: ""
        })
    }

    const handeToDoRemove=(toDoValues)=>{
       setTodoListState({
           ...todoListState,
           todos: todos.filter(todo => todo!==toDoValues) //funckaj filter zwraca obiekt, ktory jest prawda dla danego warunku
           //czyli my chcemy zeby zwrocila wszystkie obiektry poza tym przeslanym jako argument funkcji

       })
    }

    const {todos, inputValue} = todoListState;
    return(
        <div>
            <input type="text" name="item" placeholder="  add new item" value={inputValues} onChange={handleInputChange}></input>
            <button className="addButton" onClick={handleButtonClick}>Add</button>

            {!!error &&
                    <p className="error">{error}</p>
            }
            <h3>My list:</h3>
            <div className="listItem">
                {todos.map((todo)=>(<Todo key={todo} todo={todo} onCloseClick={handeToDoRemove}/>))}
            </div>
        </div>
    )
}

export default ToDoList
